    node.error("Please Update Zones First");
}
msg.payload.data.forEach(function(item){
    var pair = {
        "name": item.metadata.name,
        "id": item.id,
        // "rid": item.group.rid,
        // "rtype": item.group.rtype,
        "zone-name": zones[item.group.rid]
    };
    response.push(pair);
});
return {
    "payload": response,
    "topic": "Scene name/rids"
};

var response = [];
var rooms = flow.get("rooms") || {};
var size = Object.keys(rooms).length;
if(size <= 0) {
    node.error("Please Update Rooms First");
}
msg.payload.data.forEach(function(item){
    var pair = {
        "name": item.metadata.name,
        "type": item.metadata.archetype,
        "id": item.id,
        "color": item.hasOwnProperty("color") ? true : false,
        "temp": item.hasOwnProperty("color_temperature") ? true : false
    };
    response.push(pair);
});
return {
    "payload": response,
    "topic": "Lights"
};

var response = [];
var roomkeys = {};
msg.payload.data.forEach(function(item){
    if(item.services.length > 0) {
        var pair = {
            "name": item.metadata.name,
            "id": item.id,
            "rid": item.services[0].rid,
            "rtype": item.services[0].rtype
        };
        roomkeys[item.id] = item.metadata.name;
        response.push(pair);
    }
});

return {
    "payload": response,
    "topic": "rooms name/rid",
    "keys": roomkeys
};

// NOTE: Devices have multipe rtypes in the services
// This script grabs the first one, so a motion sensor may show
// up as "temperature" as it also does temp. don't worry
// this script is more to get the name of the devices to help
// with the button/motion scripts.
var response = [];
var devices = {};
msg.payload.data.forEach(function(item){
    try {
        if(item.metadata.archetype == "unknown_archetype") {
            var pair = {
                "name": item.metadata.name,
                "id": item.id,
                "rid": item.services[0].rid,
                "rtype": item.services[0].rtype
            };
            devices[item.id] = item.metadata.name;
            response.push(pair);
        }
    } catch(err) {
        node.warn(err);
    }
});

return {
    "payload": response,
    "topic": "devices name/rid",
    "keys": devices
};

