// Based on payload type, define whether current light must be set ON or OFF
// Ensure we have a valid payload object to work on
if (msg.payload === undefined) {
    msg.payload = {};
} else if (typeof (msg.payload) === 'string') {
    try { msg.payload = JSON.parse(msg.payload); } catch (error) { }
}

// ON/OFF & Brightness state ?
let onState;
let brightnessState;
if (typeof (msg.payload) === 'object') {
    if (typeof (msg.payload.on) === 'boolean') {
        onState = msg.payload.on;
    }
    if (typeof (msg.payload.brightness) === 'number') {
        brightnessState = msg.payload.brightness;
    }
} else {
    if (typeof (msg.payload) === 'boolean') {
        onState = msg.payload;
    } else if (!isNaN(msg.payload)) {
        if (msg.payload == 0) {
            onState = false;
        } else {
            onState = true;
            brightnessState = parseInt(msg.payload);
        }
    };
    msg.payload = {};
}

// Build Payload to be used and sent
if (onState !== undefined) {
    msg.payload.on = { 'on': onState };
}
if (brightnessState !== undefined) {
    msg.payload.dimming = { 'brightness': brightnessState };
}

return msg;

