// ACTION REQUIRED : You can copy the user key from the hue config node out of Huemagic
let user = flow.get('HUEBridge_Key');
let ip = flow.get('HUEBridge_IP');

// DO NOT CHANGE UNDERNEATH
if(msg.endpoint === undefined)
 msg.url = 'https://' + ip + '/clip/v2/resource';
else if(msg.topic === undefined)
 msg.url = 'https://' + ip + '/clip/v2/resource/' + msg.endpoint;
else
 msg.url = 'https://' + ip + '/clip/v2/resource/' + msg.endpoint + "/" + msg.topic;

msg.headers = {};

msg.headers['hue-application-key'] = user;
msg.headers['Content-Type'] = 'application/json';
msg.headers['Accept'] = 'application/json';

if (msg.payload != undefined) {
    msg.headers['ContentLength'] = JSON.stringify(msg.payload).length;
}
if (msg.verb === undefined) {
    msg.method = "get";
} else {
    msg.method = msg.verb;
}

return msg;

// Take a copy of payload & headers as they are now to be able to re-use it on retry
if (msg.retryInfo === undefined) {
    // msg was never processed befor, take a copy (snapshot) of key values to be re-applied in case of retry
    msg.retryInfo = {}
    msg.retryInfo.payload = msg.payload;
    msg.retryInfo.headers = msg.headers;
    msg.retryInfo.retryCount = 0;
} else {
    // msg was processed at least once before, apply snapshot'd values back
    msg.payload = msg.retryInfo.payload;
    msg.headers = msg.retryInfo.headers;
    msg.statusCode = undefined;
    msg.retryInfo.retryCount++
}

return msg;
